import React from 'react';
import { Button, Typography, Grid} from '@material-ui/core';
import { makeStyles, ThemeProvider} from '@material-ui/core/styles';
import {Redirect} from 'react-router-dom';
import Header from './components/header'
import NewProjectBtn from './components/newProjectBtn'
import Index from './components/index'
import LandArea from './components/landArea';

class HomePage extends React.Component {

    projectsURL = 'http://localhost:3000/api/v1/projects/'
    usersURL = 'http://localhost:3000/api/v1/users'

    state = {
        loggedIn: false,
        user: {
            id: 0,
            username: '',
            projects: []
        },
        allProjects: [],
        indexPersonal: true

    }

      componentDidMount(){
          fetch(this.projectsURL)
              .then(res => res.json())
              .then(projects => {
                  console.log(projects)
                  this.setState({allProjects: projects})})
      }
    handlePersonal = () => {
        this.setState({indexPersonal: true})
    }

    handleCommunity = () => {
        this.setState({indexPersonal: false})
    }

    renderUserProjects = (userInfo) => {
        console.log(userInfo.user_id)
        var userProjects = []
        this.state.allProjects.forEach(project => {
            if(project.user_id == userInfo.id){
                userProjects.push(project)
            }
        })
        console.log(userProjects)
    }

    createUser = e => {
        e.preventDefault()
        console.log('hello')
        fetch('http://localhost:3000/api/v1/users', {
            method: 'POST',
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify({
                name: e.target.firstname.value,
                surname: e.target.surname.value,
                username: e.target.username.value,
                password_digest: e.target.password.value,
                email: e.target.email.value
            })
        })
        .then(res => res.json())
        .then(console.log)
    }

    handleLogin = (e) => {
        e.preventDefault()
        console.log(e.target.username.value)
        fetch("http://localhost:3000/api/v1/login", {
            method: "POST",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify({
                username: e.target.username.value,
                password_digest: e.target.password.value
            })
        })
        .then(res => res.json())
        .then(userInfo => {
            console.log(userInfo.token)
            this.renderUserProjects(userInfo)
            this.setState({user: {id: userInfo.user_id}})
            this.setState({loggedIn: true})
            localStorage.token = userInfo.token
        })
    }


    handleNewProject = (e) => {
        e.preventDefault()
        console.log('here')
        fetch('http://localhost:3000/api/v1/projects', {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
                Accept: "application/json"
            },
            body: JSON.stringify({
                user_id: this.state.user.id,
                project_name: 'pog',
                theme: 'dark',
                html: '',
                css: '',
                javascript: ''
            })
        })
        .then(res => res.json())
        .then(project => {
            console.log(project)
            this.props.history.push(`/projects`)
        })
    }

    render(){

        return(

        <div>
            <Header state={this.state} createUser={this.createUser} handleLogin={this.handleLogin}/>
            <Grid container style={{flexGrow: 1, height: "1600px", width: "100%"}}>
                <Grid item container style={{backgroundColor: "#262B3B", height: "46%"}}>
                    <LandArea state={this.state} handleNewProject={this.handleNewProject}/>
                </Grid>
                <Grid item container style={{backgroundColor: "#262B3B", height: "54%"}}>
                    <Index state={this.state} handlePersonal={this.handlePersonal} handleCommunity={this.handleCommunity}/>
                </Grid>
            </Grid>
        </div>
    );
  }
}

export default HomePage;
