import React from 'react';
import { Button, Typography, Grid} from '@material-ui/core';
import { makeStyles, ThemeProvider} from '@material-ui/core/styles';
import { useHistory } from "react-router-dom";
import Header from './components/header'
import NewProjectBtn from './components/newProjectBtn'
import Index from './components/index'
import LandArea from './components/landArea';

class HomePage extends React.Component {

    projectsURL = 'http://localhost:3000/api/v1/projects/'
    usersURL = 'http://localhost:3000/api/v1/users/'

    state = {
        user: null, 
        allProjects: []

    }

    history = useHistory();


    componentDidMount(){
        fetch(this.projectsURL)
            .then(res => res.json())
            .then(projects => {
                //setTimeout(this.setState({allProjects: projects}), 3000)
                this.setState({allProjects: projects})
                //console.log(this.state.allProjects)
            })
      }
    handlePersonal = () => {
        this.setState({indexPersonal: true})
    }

    handleCommunity = () => {
        this.setState({indexPersonal: false})
    }

    renderProjects = () => {
        console.log('hellp')
    }

    userProjects = (userInfo) => {
        return this.state.allProjects.filter(project => project.user_id === userInfo.user.id)
    }

    createUser = e => {
        e.preventDefault()
        console.log('hello')
        console.log(e.target.username.value)
        fetch('http://localhost:3000/api/v1/users', {
            method: 'POST',
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify({
                username: e.target.username.value,
                password: e.target.password.value,
                email: e.target.email.value
            })
        })
        .then(res => res.json())
        .then(console.log)
    }

    handleLogin = (e) => {
        e.preventDefault()
        console.log(e.target.username.value, e.target.password.value)
        fetch("http://localhost:3000/api/v1/login", {
            method: "POST",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify({
                username: e.target.username.value,
                password: e.target.password.value
            })
        })
        .then(res => res.json())
        .then(userInfo => {
            this.setState({user: userInfo.user})
            localStorage.token = userInfo.token

        })
    }


    handleNewProject = (e) => {
        e.preventDefault()
        console.log('here')
        fetch('http://localhost:3000/api/v1/projects', {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
                Accept: "application/json"
            },
            body: JSON.stringify({
                user_id: this.state.user.id,
                project_name: 'pog',
                theme: 'dark',
                html: '',
                css: '',
                javascript: ''
            })
        })
        .then(res => res.json())
        .then(project => {
            this.props.setProject(project)
            history.push("/project")
        })
    }

    render(){

        return(

        <div>
            <Header state={this.state} createUser={this.createUser} handleLogin={this.handleLogin}/>
            <Grid container style={{flexGrow: 1, height: "1600px", width: "100%"}}>
                <Grid item container style={{backgroundColor: "#262B3B", height: "46%"}}>
                    <LandArea state={this.state} handleNewProject={this.handleNewProject}/>
                </Grid>
                <Grid item container style={{backgroundColor: "#262B3B", height: "54%"}}>
                    <Index projects={this.state.allProjects} handlePersonal={this.handlePersonal} handleCommunity={this.handleCommunity}/>
                </Grid>
            </Grid>
        </div>
    );
  }
}

export default HomePage;
