import React from 'react';
import { Button, Typography, Grid, Paper} from '@material-ui/core';
import { makeStyles, ThemeProvider} from '@material-ui/core/styles';
import ProjectArea from './components/ProjectArea'
import './css/project.css';


class ProjectPage extends React.Component {

    state = {
        html: "",
        css: "",
        js: ""
    }

    componentDidMount(){
        project = this.props.project
        if(project.html === ""){
            this.setState({html: ""})
        }else{
            this.setState({html: `${project.html}`})
        }
        if(project.css === ""){
            this.setState({css: ""})
        }else{
            this.setState({css: `${project.css}`})
        }
        if(project.html === ""){
            this.setState({css: ""})
        }else{
            this.setState({javascript: `${project.html}`})
        }
    }

    onChange = localStorage => {
        let htmlCode = localStorage.codepenClonehtml
        let cssCode = localStorage.codepenClonecss
        let jsCode = localStorage.codepenClonejs
        console.log(htmlCode, cssCode, jsCode)
    }



    render(){
        return(
            <div>
                <ProjectArea onChange={this.onChange}/>
            </div>
        );
    }
}

export default ProjectPage;

