import React from 'react';
import { IconButton, Button, Typography, Grid,  Dialog, DialogTitle, DialogContent, TextField, DialogActions, DialogContentText} from '@material-ui/core';
import { makeStyles, ThemeProvider} from '@material-ui/core/styles';
import BlockIcon from '@material-ui/icons/Block';
import CheckCircleIcon from '@material-ui/icons/CheckCircle';
import CheckCircleOutlineIcon from '@material-ui/icons/CheckCircleOutline';
import CloseIcon from '@material-ui/icons/Close';
import CheckIcon from '@material-ui/icons/Check';
//import history from '../history';
import Header from './components/header'
import NewProjectBtn from './components/newProjectBtn'
import Index from './components/index'
import LandArea from './components/landArea';

class HomePage extends React.Component {

    projectsURL = 'http://localhost:3000/api/v1/projects/'
    usersURL = 'http://localhost:3000/api/v1/users/'

    state = {
        user: null, 
        allProjects: [],
        loginWarningOpened: false,
        logoutPop: false

    }

    componentDidMount(){
        console.log(this.props.user)
        fetch(this.projectsURL)
            .then(res => res.json())
            .then(projects => {
                setTimeout(this.setState({allProjects: projects}), 1)
                //this.setState({allProjects: projects})
                console.log(this.state.allProjects)
            })
    }

    handleLogout = () => {
        this.closeLogoutPop()
        this.setState({user: null})
        localStorage.token = null
    }

    renderProjects = () => {
        console.log('hellp')
    }

    userProjects = (userInfo) => {
        return this.state.allProjects.filter(project => project.user_id === userInfo.user.id)
    }

    createUser = e => {
        e.preventDefault()
        console.log('hello')
        console.log(e.target.username.value)
        fetch('http://localhost:3000/api/v1/users', {
            method: 'POST',
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify({
                username: e.target.username.value,
                password: e.target.password.value,
                email: e.target.email.value
            })
        })
        .then(res => res.json())
        .then(console.log)
    }

    handleLogin = (e) => {
        e.preventDefault()
        console.log(e.target.username.value, e.target.password.value)
        fetch("http://localhost:3000/api/v1/login", {
            method: "POST",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify({
                username: e.target.username.value,
                password: e.target.password.value
            })
        })
        .then(res => res.json())
        .then(userInfo => {
            console.log(this.props)
            this.setState({user: userInfo.user})
            localStorage.token = userInfo.token

        })
    }


    handleNewProject = (e) => {
        console.log('here')
        fetch('http://localhost:3000/api/v1/projects', {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
                Accept: "application/json"
            },
            body: JSON.stringify({
                user_id: this.state.user.id,
                project_name: 'pog',
                theme: 'dark',
                html: '',
                css: '',
                javascript: ''
            })
        })
        .then(res => res.json())
        .then(project => {
            this.props.setProject(project)
            this.props.history.push("/project")
        })
    }

    userProjectsDenied = () => {
        console.log('log in')
        this.setState({loginWarningOpened: true})
    }

    renderStatus = () => {
        if(this.state.user != null){
            console.log()
        }
    }

    handleLogoutPop = () => {
        this.setState({logoutPop: true})
    }

    closeLogoutPop = () => {
        this.setState({logoutPop: false})
    }

    render(){

        return(
        <>
            <Dialog open={this.handleLogoutPop} style={{color: '#262B3B'}}>
                <DialogTitle variant='h6'>Are You Sure!</DialogTitle>
                <DialogActions>
                    <IconButton onClick={closeLogoutPop}><CloseIcon style={{color: 'red'}}/></IconButton>
                    <IconButton onClick={handleLogout}><CheckIcon style={{color: 'green'}}/></IconButton>
                </DialogActions>
            </Dialog>
            <Dialog open={this.state.loginWarningOpened}>
                <DialogTitle>
                    <i class='fas fa-ban fa-7x' style={{color: 'red', marginLeft: '32px'}}/>
                </DialogTitle>
                <DialogContent>
                    <Typography variant='p' style={{alignText: 'center'}}>Please Login to gain access!</Typography>
                </DialogContent>
                <DialogActions>
                    <Button style={{marginRight: '80px'}}onClick={()=> this.setState({loginWarningOpened: false})}>Close</Button>
                </DialogActions>
            </Dialog>
            <div>
                <Header state={this.state} createUser={this.createUser} handleLogin={this.handleLogin} handleLogoutPop={this.handleLogoutPop}/>
                <Grid container style={{flexGrow: 1, height: "1600px", width: "100%"}}>
                    <Grid item container style={{backgroundColor: "#262B3B", height: "46%"}}>
                        <LandArea state={this.state} userProjectsDenied={this.userProjectsDenied} handleNewProject={this.handleNewProject}/>
                    </Grid>
                    <Grid item container style={{backgroundColor: "#262B3B", height: ""}}>
                        <Index userProjectsDenied={this.userProjectsDenied} user={this.state.user} setProject={this.props.setProject} history={this.props.history} projects={this.state.allProjects}/>
                    </Grid>
                </Grid>
            </div>
        </>
    );
  }
}

export default HomePage;
