import React from 'react';
import { Button, Typography, Grid} from '@material-ui/core';
import { makeStyles, ThemeProvider} from '@material-ui/core/styles';
import { LiveProvider, LiveEditor, LivePreview, LiveError } from "react-live";
import { transform } from "@babel/core";
import 'codemirror/lib/codemirror.css'
import 'codemirror/theme/material.css'

import Header from './components/Header';
import Editor from './components/Editor';
import Console from './components/Console'

class ProjectPage extends React.Component {

    state = {
        editorValue: ''
    }

    onEditorChange = (value) => {
        this.state.editorValue = value
    }

    onRun = () => {
        alert(this.state.editorValue)
    }

    render(){
        const test = `
        class TransformExample extends React.Component {

        boop = () => {
            console.log('boop')
        }

        render() {
            return (
            <center>
                <button onClick={this.boop}><h3>Boop!</h3></button>
            </ceçnter>
            )
        }
        }
        `;
        return(
            <div>
                <Grid container style={{backgroundColor: 'powderblue'}}>  
                    <Grid item container container style={{backgroundColor: 'powderblue', height: '63px'}}>
                            <Header />
                    </Grid>
                </Grid>
                <Grid container style={{backgroundColor: 'grey', height: '730px'}}>
                        <Grid container style={{backgroundColor: 'pink', height: '99.99%', width: '33.33%'}}>
                            <h1>first</h1>
                        </Grid>
                        <LiveProvider
                            code={test}
                            transformCode={code => {
                            const transformed = transform(code, {
                                plugins: [
                                require("@babel/plugin-syntax-jsx"),
                                [
                                    require("@babel/plugin-proposal-class-properties"),
                                    { loose: true }
                                    ]
                                ]
                            }).code;

                                return transformed;
                            }}
                        >
                            <div style={{backgroundColor: 'purple', height: '99.99%', width: '33.33%'}}>
                                <LiveEditor />
                            </div>
                            <Grid container style={{backgroundColor: 'powderblue', height: '99.99%', width: '33.33%'}}>
                                <LivePreview />
                            </Grid>
                            <LiveError />
                        </LiveProvider>
                </Grid>
            </div>

        );
    }
}

export default ProjectPage

