import React, { useState, useEffect } from 'react';
import Editor from './Editor'
import useLocalStorage from './hooks/useLocalStorage'
import { Button, Typography, Grid, Paper, Dialog, DialogTitle, DialogContent, TextField, DialogActions, DialogContentText} from '@material-ui/core';

function ProjectArea(props) {
  const [html, setHtml] = useLocalStorage('html', '')
  const [css, setCss] = useLocalStorage('css', '')
  const [js, setJs] = useLocalStorage('js', '')
  const [srcDoc, setSrcDoc] = useState('')
  const [open, setOpen] = React.useState(false);

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const handleSave = event => {
      setOpen(false);
      console.log(event.target)
  }

  useEffect(() => {
    const timeout = setTimeout(() => {
      setSrcDoc(`
        <html>
          <body>${html}</body>
          <style>${css}</style>
          <script>${js}</script>
        </html>
      `)
      props.onChange(localStorage)
    }, 250)

    return () => clearTimeout(timeout)
  }, [html, css, js])

  return (
    <>
      <Button 

      style={{backgroundColor: "hsl(225, 6%, 25%", borderRadius: "0px", color: "powderblue"}}
      variant="outlined"
      onClick={handleClickOpen}>
          Save
      </Button>
      <Button onClick={this.props.history.push(`/`)}>
        Return
      </Button>
      <Dialog open={open} onClose={handleClose}>
          <DialogTitle id="save-form">Save Project</DialogTitle>
          <DialogContent>
          <DialogContentText>
            Please provide a Project Name
            so we can save your terrible code.
          </DialogContentText>
          <TextField
            autoFocus
            margin="dense"
            id="projectName"
            label="Project Name here..."
            type="email"
            fullWidth
            />
          </DialogContent>
          <DialogActions>
          <Button onClick={handleClose} color="primary">
            Cry
          </Button>
          <Button onClick={handleSave} color="primary">
            Save
          </Button>
        </DialogActions>
      </Dialog>
      <div className="pane top-pane">
        <Editor
          language="xml"
          displayName="HTML"
          value={html}
          onChange={setHtml}
        />
        <Editor
          language="css"
          displayName="CSS"
          value={css}
          onChange={setCss}
        />
        <Editor
          language="javascript"
          displayName="JS"
          value={js}
          onChange={setJs}
        />
      </div>
      <div className="pane">
        <iframe
          srcDoc={srcDoc}
          title="output"
          sandbox="allow-scripts"
          frameBorder="0"
          width="100%"
          height="100%"
        />
      </div>
    </>
  )
}

export default ProjectArea;
