import React from 'react';
import {Menu, MenuItem, IconButton, AppBar, Toolbar, Typography, Grid, Button, Dialog, DialogTitle, DialogContent, TextField, DialogActions, DialogContentText} from '@material-ui/core';
import { makeStyles, ThemeProvider} from '@material-ui/core/styles';
import AccountCircleIcon from '@material-ui/icons/AccountCircle';
import CheckCircleIcon from '@material-ui/icons/CheckCircle';
import CheckCircleOutlineIcon from '@material-ui/icons/CheckCircleOutline';
import './forms.css'
import { Formik, Field, Form } from 'formik';
import { mixed, number, object, string} from 'yup';
import * as Yup from 'yup';
import { red } from '@material-ui/core/colors';
import { ContactlessOutlined } from '@material-ui/icons';


const Header = (props) => {

    const [openLogin, setOpenLogin] = React.useState(false);

    const [anchorEl, setAnchorEl] = React.useState(null);
    const open = Boolean(anchorEl);

    const handleMenu = (event) => {
        if(props.state.user != null){
            setAnchorEl(event.currentTarget);
        }else{
            var circle = document.querySelector('.accountcircle')
            console.log(circle)
            circle.style.color = 'red';
            setTimeout(circle.style.color = 'white', 3000)
        }
    };

    const handleClose = () => {
        setAnchorEl(null);
    };

    const handleClickOpenLogin = () => {
        setOpenLogin(true);
    };

      const handleCloseLogin = () => {
        setOpenLogin(false);
    };

    const [openSignUp, setOpenSignUp] = React.useState(false);

    const handleClickOpenSignUp = () => {
        setOpenSignUp(true);
    }

    const handleCloseSignUp = () => {
        setOpenSignUp(false);
    }


    const navBar = makeStyles({
        title: {
            marginRight: "1190px"
        },
        loginButton: {
            fontSize: "14",
            fontFamily: "IBM plex sans",
            marginRight: "15px",
            variant: "outlined"
        },
        signUpButton: {

            backgroundColor: "#3485E4",
            marginRight: "-10px"
        }
    });

    const classes = navBar();

    return(
        <AppBar boxShadow={3} position="static" width="100%" style={{ margin: 0 }}>
                <Toolbar>
                        <IconButton
                            aria-label="account of current user"
                            aria-controls="menu-appbar"
                            aria-haspopup="true"
                            onClick={handleMenu}
                            color="inherit"
                        >
                            <AccountCircleIcon className='accountcircle' style={{}}/>
                        </IconButton>
                        <Menu
                            id="menu-appbar"
                            anchorEl={anchorEl}
                            anchorOrigin={{
                            vertical: 'top',
                            horizontal: 'right',
                            }}
                            keepMounted
                            transformOrigin={{
                            vertical: 'top',
                            horizontal: 'right',
                            }}
                            open={open}
                            onClose={handleClose}
                        >
                            <MenuItem onClick={handleClose}>My Account</MenuItem>
                            <MenuItem onClick={handleClose}>Logout</MenuItem>
                        </Menu>
                        <Typography className={classes.title}></Typography>
                        <Button 
                            className={classes.loginButton} 
                            style={{backgroundColor: "#262B3B", color: "#3485E4"}}
                            boxShadow={3} 
                            onMouseEnter={event=>{
                                event.target.style.backgroundColor = "#3485E4" 
                                event.target.style.color = "white"
                            }}
                            onMouseLeave={event=>{
                                event.target.style.backgroundColor = "#262B3B"
                                event.target.style.color = "#3485E4"
                            }}
                            onClick={handleClickOpenLogin}
                            >Log in</Button>
                            <Dialog open={openLogin} onClose={handleCloseLogin}>
                                <DialogTitle id="login" style={{marginLeft: '180px'}}>Login</DialogTitle>
                                <DialogContent>
                                    {/* <Formik
                                    validationSchema={
                                        object({

                                        })
                                    }
                                    initialValues={{
                                        username: '',
                                        password: ''
                                    }}
                                    onSubmit={(values) => {
                                        console.log(props)
                                        handleCloseLogin();
                                    }}
                                    >
                                    {({values}) => (
                                        <Form>
                                            <Field name='username' as={TextField} label='Username' fullWidth/>
                                            <Field name='password' as={TextField} label='Password' fullWidth/>
                                            <Button type='submit' fullWidth style={{marginTop: '20px', backgroundColor: 'blue'}}>Sign Up</Button>
                                        </Form>
                                    )}
                                    </Formik> */}
                                    <div id='loginWrapper'>
                                        <div className='login-form-container'>
                                            <form onSubmit={props.handleLogin}>
                                                <div className='login-input-group'>
                                                    <i className='fas fa-user'/>
                                                    <input type="text" name="username"/>
                                                    <span className='login-bar'/>
                                                </div>
                                                <div className='login-input-group'>
                                                    <i className='fas fa-envelope'/>
                                                    <input type="text" name="password"/>
                                                    <span className='login-bar'/>
                                                </div>
                                                <div className='login-input-group'>
                                                    <i className='fas fa-'/>
                                                    <input type="submit" value="Submit"/>
                                                    <span className='login-bar'/>
                                                </div>
                                            </form>
                                        </div>
                                    </div>
                                </DialogContent>
                        </Dialog>
                        <Button className={classes.signUpButton} boxShadow={3} onClick={handleClickOpenSignUp}>Sign up</Button>
                        <Dialog open={openSignUp} onClose={handleCloseSignUp}>
                                <DialogTitle id="signup" style={{marginLeft: '243px'}}>Sign Up</DialogTitle>
                                    <DialogContent>
                                    <div className='signupWrapper'>
                                            <div className='signup-form-container'>
                                                <form onSubmit={props.createUser}>
                                                    <div className="signup-input-group">
                                                        <i class="fas fa-user"/>
                                                        <input type="text" name="username" placeholder="Username..."/>
                                                        <span className='signup-bar'/>
                                                    </div>
                                                    <div className="signup-input-group">
                                                        <i class="fas fa-envelope"/>
                                                        <input type="text" name="email" placeholder="Email..."/>
                                                        <span className='signup-bar'/>
                                                    </div>
                                                    <div className="signup-input-group">
                                                        <i className="fas fa-lock"/>
                                                        <input type="text" name="password" placeholder="Password..."/>
                                                        <span className='signup-bar'/>
                                                    </div>
                                                    <div className="signup-input-group">
                                                        <i class="fas fa-lock"/>
                                                        <input type="password" name="passwordConf" placeholder="Confirm password..."/>
                                                        <span className='signup-bar'/>
                                                    </div>
                                                    <div className="signup-input-group">
                                                        <i class="fas fa-telegram-plane"/>
                                                        <input type="submit" value="submit"/>
                                                        <span className='signup-bar'/>
                                                    </div>
                                                </form>
                                            </div>
                                        </div>
                                        {/* <Formik 
                                        validationSchema={
                                            object({

                                            })
                                        }
                                        initialValues={{
                                            firstname: '',
                                            surname: '',
                                            email: '',
                                            username: '',
                                            password: ''
                                        }}
                                        onSubmit={(event, values)=>{
                                            console.log(values)
                                            handleCloseSignUp();
                                            props.createUser()
                                            fetch('http://localhost:3000/api/v1/users', {
                                                method: 'POST',
                                                headers: {
                                                    "Content-Type": "application/json",
                                                },
                                                body: JSON.stringify({
                                                    name: values.firstname,
                                                    surname: values.surname,
                                                    username: values.username,
                                                    password: values.password,
                                                    email: values.email
                                                })
                                            })
                                            .then(res => res.json())
                                            .then(console.log)
                                        }}>
                                            {({values}) => (
                                                <Form>
                                                    <Field name='firstname' as={TextField} label='First Name' fullWidth/>
                                                    <Field name='surname' as={TextField} label='Last Name' fullWidth/>
                                                    <Field name='email' as={TextField} label='Email' fullWidth/>
                                                    <Field name='username' as={TextField} label='Username' fullWidth/>
                                                    <Field name='password' as={TextField} label='Password' type='password' fullWidth/>
                                                    <Button type='submit' fullWidth style={{marginTop: '20px', backgroundColor: 'blue'}}>Sign Up</Button>
                                                </Form>
                                            )}
                                        </Formik> */}
                                    </DialogContent>
                        </Dialog>
                </Toolbar>
        </AppBar>
    );
}

export default Header;
